import { useState, useEffect } from "react";


export default function App (){

const[users, setUsers] = useState([])
const[next, setNext] = useState(1)

useEffect( function (){
fetch('https://api.github.com/search/repositories?q=react&per_page=10&page='+next)
.then(preResult => preResult.json())
.then(result => setUsers(result.items))
console.log(users);

}, [next])


function increment(){
    setNext(next+1)
 }


 function decrement(){
    setNext(next-1)
 }



return(
<div className="main">
    <button onClick={increment}>next</button>
    <button  onClick={decrement}>prev</button>

    {users.map(repo => <div key={repo.id} className="list">{repo.id +"- -"+ repo.full_name} <a
            href={repo.html_url}>Repositories URL</a> </div> )}
</div>
)
}
